import React, { Component } from 'react';

import GridParent from '../GridParent';
import * as C from './style';

class GridChild extends Component {
  render() {
    const { id, children, direction, grow, shrink, basis, order } = this.props;

    if (children) {
      return <GridParent id={id} children={children} direction={direction} />;
    } else {
      return (
        <C.Wrapper
          id={id}
          grow={grow}
          shrink={shrink}
          basis={basis}
          order={order}
        >
          <span>{id}</span>
          <span>{grow && `grow: ${grow}`}</span>
          <span>{shrink && `shrink: ${shrink}`}</span>
          <span>{basis && `basis: ${basis}`}</span>
          <span>{order && `order: ${order}`}</span>
        </C.Wrapper>
      );
    }
  }
}

export default GridChild;
