import styled from 'styled-components';

export const Wrapper = styled.div`
  display: flex;
  border: 1px solid blue;
  flex: 1;
  flex-direction: ${props => props.direction};
  ${props => props.grow && `flex-grow: ${props.grow};`}
`;
