import styled from 'styled-components';

export const Container = styled.div`
  border: 1px solid #ccc;
  display: flex;
  flex-wrap: wrap;

  ${props => props.direction && `flex-direction: ${props.direction};`}
  ${props => (props.width ? `flex-basis: ${props.width};` : 'flex: 1')}
`;
