import React, { useState } from 'react';
import { Container, Row, Col, ButtonGroup } from 'reactstrap';
import ReactJson from 'react-json-view';

import * as gridConfig from './sample.json';

import Grid from './components/Grid';

import * as A from './style';

function App() {
  const [themeMode, selectThemeMode] = useState('light');
  const ThemeContext = React.createContext(themeMode);

  console.log('GRID CONFIG =====', gridConfig);

  return (
    <ThemeContext.Provider value={themeMode}>
      <A.Wrapper theme={themeMode}>
        <Container>
          <ButtonGroup>
            <A.Button
              theme={themeMode}
              isActive={themeMode === 'light'}
              onClick={() => selectThemeMode('light')}
            >
              Light
            </A.Button>
            <A.Button
              theme={themeMode}
              isActive={themeMode === 'dark'}
              onClick={() => selectThemeMode('dark')}
            >
              Dark
            </A.Button>
          </ButtonGroup>

          <Row>
            <Col md="6">
              <ReactJson src={gridConfig} />
            </Col>
            <Col md="6">
              <Grid />
            </Col>
          </Row>
        </Container>
      </A.Wrapper>
    </ThemeContext.Provider>
  );
}
export default App;
