import React, { useState } from 'react';
import { Container, ButtonGroup } from 'reactstrap';
import Viewer from 'react-code-viewer';
import { highlight, languages } from 'prismjs/components/prism-core';
import 'prismjs/components/prism-clike';
import 'prismjs/components/prism-javascript';

import * as gridConfig from './config/grid';

import { GridParent, GridChild, Grid } from './components/Grid';

import * as A from './style';

function App() {
  const [themeMode, selectThemeMode] = useState('dark');
  const ThemeContext = React.createContext(themeMode);

  const code = `function add(a, b) {
    return a + b;
  }
  `;

  return (
    <ThemeContext.Provider value={themeMode}>
      <A.Wrapper theme={themeMode}>
        <Container>
          <A.Panel>
            <ButtonGroup>
              <A.Button
                theme={themeMode}
                active={themeMode === 'light'}
                onClick={() => selectThemeMode('light')}
              >
                Light
              </A.Button>
              <A.Button
                theme={themeMode}
                active={themeMode === 'dark'}
                onClick={() => selectThemeMode('dark')}
              >
                Dark
              </A.Button>
            </ButtonGroup>
          </A.Panel>

          <A.Panel>
            <h1>Flexible Grid</h1>
            <h3>Usage</h3>

            <A.Panel>
              <Viewer
                value={code}
                // onValueChange={code => this.setState({ code })}
                highlight={code => highlight(code, languages.js)}
                padding={10}
                style={{
                  fontFamily: '"Fira code", "Fira Mono", monospace',
                  fontSize: 12
                }}
              />
            </A.Panel>
          </A.Panel>

          <A.ConfigPanel>
            <GridParent>
              <GridChild>
                <A.JSONPrettier
                  id="json-pretty"
                  data={gridConfig}
                  theme={themeMode}
                ></A.JSONPrettier>
              </GridChild>
              <GridChild>
                <Grid />
              </GridChild>
            </GridParent>
          </A.ConfigPanel>
        </Container>
      </A.Wrapper>
    </ThemeContext.Provider>
  );
}
export default App;
