import styled from 'styled-components';
import * as colors from './constants/colors';
import { Button as BSButton } from 'reactstrap';
import JSONPretty from 'react-json-pretty';

export const Wrapper = styled.div`
  height: 100vh;
  padding-top: 3%;
  background: ${props => colors[props.theme].background};
`;

export const Button = styled(BSButton)`
  background: ${props => {
    let color;

    if (props.active) {
      color = `${colors[props.theme].primary} !important;`;
    } else {
      color = `${colors[props.theme].secondary} !important;`;
    }
    return color;
  }}
  border-color: ${props => {
    let color;

    if (props.active) {
      color = `${colors[props.theme].primary} !important;`;
    } else {
      color = `${colors[props.theme].secondary} !important;`;
    }
    return color;
  }}
`;

export const Panel = styled.div`
  height: 900px;
  overflow: hidden;
`;

export const JSONPrettier = styled(JSONPretty)`
  border: 1px solid red;
  overflow: scroll;
  margin: 0 10px;
  border-radius: 5px;
  background: ${props => colors[props.theme].codeBackground};

  pre {
    color: ${props => colors[props.theme].codeColor};
  }
`;
