import styled from 'styled-components';

export const Wrapper = styled.div`
  border: 1px solid green;
  display: flex;

  ${props => (props.grow ? `flex-grow: ${props.grow};` : 'flex: 1;')}
  ${props => (props.shrink ? `flex-shrink: ${props.shrink};` : 'flex: 1;')}
`;
